import { useState, useEffect } from 'react';

const ShowResults = ({ game, Results, GameState, setGameState }) => {
    const [message, setMessage] = useState("");
    const [animation, setAnimation] = useState("");

    useEffect = (() => {
        if (game !== "inProgress" && game !== "placeBets") {
            if (game === Results.blackjack) {
                setMessage("Blackjack!")
                setAnimation(Win);
                setGameState(GameState.bet)
            };
                if (game === Results.playerBusts) {
                    setMessage("Player Busted!")
                    setAnimation(Lose);
                    setGameState(GameState.bet)
                };
                    if (game === Results.dealerBusts) {
                        setMessage("Dealer Busted!")
                        setAnimation(Win);
                        setGameState(GameState.bet)
                    };
                        if (game === Results.playerWins) {
                            setMessage("Player Wins!")
                            setAnimation(Win);
                            setGameState(GameState.bet)
                        };
                            if (game === Results.dealerWins) {
                                setMessage("Dealer Wins!")
                                setAnimation(Win);
                                setGameState(GameState.bet)
                            };
                                if (game === Results.push) {
                                    setMessage("Push!")
                                    setGameState(GameState.bet)
                                }; 
        }}, [game])
    return(
    <><div>{message}</div>
    <div>{animation}</div></>
    );
}

    export default ShowResults
