import React, { useState, useEffect } from 'react';
import styledCom from 'styled-components';
    import Win from '../../imgs/gifs/_blowing_kiss[window]4x.gif';
    import Lose from '../../imgs/gifs/_laughing_devil[window]4x.gif';


const ShowResults = ({ game, Results, GameState, reset, setGameState }) => {
    const [message, setMessage] = useState("");
    const [animation, setAnimation] = useState("");
    const resetGame = () => {
        setTimeout(() => reset(), [2000])
        setTimeout(() => setMessage(""), [2000])
        setTimeout(()=> setAnimation(""), [2000])
    };

    if (game === Results.blackjack) {
        setMessage("Blackjack!")
        setAnimation(Win);
        resetGame();
    } else
    if (game === Results.playerBusts) {
        setMessage("Player Busted!")
        setAnimation(Lose);
        resetGame();
    } else
    if (game === Results.dealerBusts) {
        setMessage("Dealer Busted!")
        setAnimation(Win);
        resetGame();
    } else
    if (game === Results.playerWins) {
        setMessage("Player Wins!")
        setAnimation(Win);
        resetGame();
    } else
    if (game === Results.dealerWins) {
        setMessage("Dealer Wins!")
        setAnimation(Win);
        resetGame();
    } else
    if (game === Results.push) {
        setMessage("Push!")
        resetGame();
    }; 

    return(
    <><Message>{message}</Message>
    <Animation>{animation}</Animation>
    </>
    )
}

    export default ShowResults;

    const Message = styledCom.div`
    bottom:15%;
    `;

    const Animation = styledCom.div`

    `;
