const Visuals = ({balance, betSize, gameState, GameState, handleClear, handleMax, handleClick}) => {
// console.table({playerHand});
// console.table({dealerHand});
    return (
      <>
        <BlackjackTable/>
          <Displays 
            balance={balance} 
            betSize={betSize}/>
          <Bet
            gameState={gameState}
            GameState={GameState}
            handleClick={handleClick}
            handleClear={handleClear}
            handleMax={handleMax}/>
        <LeaveTable/>
      </>
    );
}

